import { View, Text , FlatList} from 'react-native'
import React , {useEffect , useState} from 'react'
import Header from "../../components/header";
import styles from './style';
import category from "../../assets/data/category.json";
import CategoryCard from "../../components/categoryCard/index";
import { useNavigation , useRoute} from "@react-navigation/native";
import { Departments } from '../../network/config';
import axios from "axios";
export default function CategoryScreen() {
  const navigation = useNavigation();
  const [departments , setDepartments] = useState();
  useEffect(()=>{
    loadDepartments ()
   
  },[])
  function loadDepartments () {
    axios.get('http://192.168.73.113:1337/api/departments?populate=*')
      .then(({ data }) => {
        setDepartments(data.data);
        
      })
      .catch((e) => {
        console.error(e);
        setError('An error occurred, please try again later.');
      });
  }
 console.log(depa)
  return (
    <View style={{flex:1 , marginBottom:100}}>
     <Header />
     <View style={styles.header}>
       <Text style={styles.text}>
         Category
       </Text>
     </View>
     <View>
     <FlatList
          numColumns={2}
          data={category}
          keyExtractor={(item) => item.id}
          renderItem={({ item }) => ( <CategoryCard category={item.attributes} onPressHandler={()=>{navigation.navigate('Filter',{category:item})}}/>)}
        />
      </View>
     </View>
    
  )
}
