import { Departments } from "./config";
import axios from 'axios'
export default class API  {
   
  async fetchDepartments(){
    
    let response = await fetch (Departments);
    if(response.ok)
    {
        let data = await response.json();
        return data.data;
    }
    else
    {
        console.log('error');
    }
    
  }
}
